parameters.jsCode:
// 1. Obtenemos TODOS los datos de Supabase y de Edit Fields
const allOffers = $('Get Offers').all();
const editFields = $('Edit Fields').first().json;
const targetProvider = editFields.provider_name;
const qaItems = $('Edit Fields').first().json.qa_items || [];

let issuesFound = [];

// 2. Iteramos por todos los registros
for (const item of allOffers) {
  const row = item.json;

  // Solo procesamos si el proveedor coincide
  if (row.provider_name === targetProvider) {
    const status = (row.evaluation_value || '').trim();

    // CRITERIO: Si es distinto de "INCLUIDO" (ignorando mayúsculas/minúsculas), es un ISSUE
    if (status.toUpperCase() !== 'INCLUIDO') {
      // Usar descripción legible en lugar de UUID
      const description = row.requirement_text || row.item_description || row.comment || 'Item requires clarification';
      issuesFound.push({
        description: description,
        status: status || 'NOT PROVIDED',
        provider_comment: row.comment || ''
      });
    }
  }
}

// 3. Preparar Q&A questions si existen
const qaQuestions = qaItems.map(q => ({
  question: q.question || q.pregunta_texto,
  discipline: q.discipline || q.disciplina,
  importance: q.importance || q.importancia
}));

// 4. Devolvemos un solo objeto con toda la info para el LLM
return {
  issues: issuesFound,
  total_issues: issuesFound.length,
  qa_questions: qaQuestions,
  total_qa_questions: qaQuestions.length,
  project_id: editFields.project_id,
  project_name: editFields.project_name,
  provider_name: targetProvider,
  tone: editFields.tone
};

