// Convert markdown body to HTML for email
const markdownBody = $('Extract Fields').first().json.email_body;

// Simple markdown to HTML conversion
let htmlBody = markdownBody
  // Convert bold
  .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
  // Convert italic
  .replace(/\*(.+?)\*/g, '<em>$1</em>')
  // Convert bullet points
  .replace(/^- (.+)$/gm, '<li>$1</li>')
  // Wrap lists
  .replace(/(<li>.*<\/li>\n?)+/g, '<ul>$&</ul>')
  // Convert line breaks to paragraphs
  .split('\n\n')
  .map(p => p.trim())
  .filter(p => p.length > 0)
  .map(p => {
    if (p.startsWith('<ul>') || p.startsWith('<li>')) return p;
    return `<p>${p}</p>`;
  })
  .join('\n');

// Add email styling
const styledHtml = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; max-width: 800px; margin: 0 auto; padding: 20px; }
    p { margin: 0 0 16px 0; }
    ul { margin: 0 0 16px 0; padding-left: 24px; }
    li { margin: 4px 0; }
    strong { font-weight: 600; }
  </style>
</head>
<body>
${htmlBody}
</body>
</html>
`;

return {
  html_body: styledHtml,
  to: $('Extract Fields').first().json.to,
  subject: $('Extract Fields').first().json.subject,
  provider_name: $('Extract Fields').first().json.provider_name,
  project_id: $('Extract Fields').first().json.project_id,
  project_name: $('Extract Fields').first().json.project_name
};

// Generate a random token without crypto module
function generateToken(length = 64) {
  const chars = 'abcdef0123456789';
  let token = '';
  for (let i = 0; i < length; i++) {
    token += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  // Add timestamp for uniqueness
  const timestamp = Date.now().toString(16);
  return (timestamp + token).slice(0, 64);
}

const token = generateToken();

// Calculate expiration date
const expiresDays = $input.first().json.expires_days || 7;
const expiresAt = new Date();
expiresAt.setDate(expiresAt.getDate() + expiresDays);

return {
  json: {
    ...$input.first().json,
    token: token,
    expires_at: expiresAt.toISOString()
  }
};

// Split question_ids array into individual items
const questionIds = $("Set Input Params2").first().json.question_ids || [];

// Return each ID as a separate item
return questionIds.map(id => ({
  json: {
    question_id: id
  }
}));

// Split question_ids for update
const questionIds = $("Set Input Params2").first().json.question_ids || [];
return questionIds.map(id => ({ json: { question_id: id } }));

