// =============================================
// NODO: Filter Deficiencies Only
// Solo incluye items que realmente son deficiencias
// =============================================

const items = $input.all();

// Valores que indican deficiencias (necesitan Q&A)
const DEFICIENCY_VALUES = [
  'PARTIALLY INCLUDED',
  'NOT INCLUDED WITH ALTERNATIVE',
  'NOT INCLUDED',
  'NO INFORMATION'
];

// Valores que NO son deficiencias (no necesitan Q&A)
const OK_VALUES = [
  'INCLUDED',
  'INCLUDED WITH CAVEATS'
];

const filteredItems = items.filter(item => {
  const evalValue = (item.json.evaluation_value || '').toUpperCase().trim();

  // Si es un valor OK, excluir
  if (OK_VALUES.some(v => evalValue === v.toUpperCase())) {
    return false;
  }

  // Caso 1: Es uno de los valores de deficiencia conocidos
  if (DEFICIENCY_VALUES.some(v => evalValue.includes(v.toUpperCase()))) {
    return true;
  }

  // Caso 2: Empieza con "NO VALUE FOUND" (evaluación económica sin valor)
  if (evalValue.startsWith('NO VALUE FOUND')) {
    return true;
  }

  // Caso 3: Score bajo (menor a 5) como indicador de deficiencia
  const score = item.json.score || 0;
  if (score < 5) {
    return true;
  }

  // Por defecto, excluir (valores económicos con precios, etc.)
  return false;
});

console.log(`📋 Filtrado de deficiencias:`);
console.log(`   ├─ Total recibidos: ${items.length}`);
console.log(`   └─ Deficiencias encontradas: ${filteredItems.length}`);

// Log de valores excluidos para debug
const excluded = items.length - filteredItems.length;
if (excluded > 0) {
  console.log(`   ├─ Excluidos (valores OK o económicos): ${excluded}`);
}

return filteredItems;

