// Extract LLM output and structure for database (v6 - Dynamic Criteria)
const input = $input.first().json;
const llmOutput = input.output || input;

// Extract category scores
const cs = llmOutput.category_scores || {};
const getScore = (cat) => {
    if (!cs[cat]) return 0;
    return (typeof cs[cat] === 'object' && cs[cat].score) ? cs[cat].score : (cs[cat] || 0);
};

// Extract individual scores (dynamic - works with any criteria)
const is = llmOutput.individual_scores || {};
const configType = input.config_type || llmOutput.config_type || 'default';
const scoringConfig = input.scoring_config || null;

// Build category_scores as a clean object { category_name: score }
const categoryScoresClean = {};
for (const [catKey, catVal] of Object.entries(cs)) {
    categoryScoresClean[catKey] = (typeof catVal === 'object' && catVal.score) ? catVal.score : (catVal || 0);
}

// Build ranking object - always include legacy fields for backward compat
// plus the new JSONB fields for dynamic scoring
const ranking = {
    provider_name: llmOutput.provider_name || 'UNKNOWN',
    project_id: input.project_id || llmOutput.project_id || null,
    // Legacy fixed fields (populated from dynamic data when possible)
    technical_score: getScore('TECHNICAL') || getScore('technical') || 0,
    economic_score: getScore('ECONOMIC') || getScore('economic') || 0,
    execution_score: getScore('EXECUTION') || getScore('execution') || 0,
    hse_compliance_score: getScore('HSE_COMPLIANCE') || getScore('hse_compliance') || 0,
    scope_facilities_score: is.scope_facilities || 0,
    scope_work_score: is.scope_work || 0,
    deliverables_quality_score: is.deliverables_quality || 0,
    total_price_score: is.total_price || 0,
    price_breakdown_score: is.price_breakdown || 0,
    optionals_included_score: is.optionals_included || 0,
    capex_opex_methodology_score: is.capex_opex_methodology || 0,
    schedule_score: is.schedule || 0,
    resources_allocation_score: is.resources_allocation || 0,
    exceptions_score: is.exceptions || 0,
    safety_studies_score: is.safety_studies || 0,
    regulatory_compliance_score: is.regulatory_compliance || 0,
    // Dynamic JSONB fields
    category_scores_json: categoryScoresClean,
    individual_scores_json: is,
    evaluation_details: {
        strengths: llmOutput.strengths || [],
        weaknesses: llmOutput.weaknesses || [],
        recommendations: llmOutput.recommendations || [],
        summary: llmOutput.evaluation_summary || ''
    },
    overall_score: llmOutput.overall_score || 0,
    compliance_percentage: llmOutput.compliance_percentage || 0,
    evaluation_count: 1,
    last_updated: new Date().toISOString()
};

return [{
    json: {
        ranking,
        details: ranking.evaluation_details,
        category_scores: cs,
        individual_scores: is,
        config_type: configType,
        scoring_config: scoringConfig
    }
}];

// =============================================
// NODE: Generate Final Ranking with Details (v6 - Fully Dynamic Criteria)
// Passes individual_scores and category scores as dynamic objects
// =============================================

const items = $input.all();
let allRankings = [];

items.forEach(item => {
    if (item.json.all_rankings) {
        allRankings = allRankings.concat(item.json.all_rankings);
    } else if (item.json.ranking) {
        allRankings.push(item.json);
    }
});

const sortedRankings = allRankings
    .map(r => ({
        ranking: r.ranking || r,
        details: r.details || {},
        category_scores: r.category_scores || {},
        individual_scores: r.individual_scores || {},
        config_type: r.config_type || 'default'
    }))
    .sort((a, b) => (b.ranking.overall_score || 0) - (a.ranking.overall_score || 0));

const finalRanking = sortedRankings.map((r, idx) => {
    // Build dynamic category scores object
    const categoryScores = r.ranking.category_scores_json || {};
    // Also populate legacy fields for backward compatibility
    const scores = {
        technical: categoryScores.TECHNICAL || categoryScores.technical || r.ranking.technical_score || 0,
        economic: categoryScores.ECONOMIC || categoryScores.economic || r.ranking.economic_score || 0,
        execution: categoryScores.EXECUTION || categoryScores.execution || r.ranking.execution_score || 0,
        hse_compliance: categoryScores.HSE_COMPLIANCE || categoryScores.hse_compliance || r.ranking.hse_compliance_score || 0
    };
    // Add any dynamic categories that aren't in the legacy set
    for (const [catKey, catScore] of Object.entries(categoryScores)) {
        const normalizedKey = catKey.toLowerCase();
        if (!scores[normalizedKey]) {
            scores[normalizedKey] = catScore;
        }
    }

    // Build dynamic individual_scores from JSONB or fallback to legacy fields
    const dynamicIndividual = r.ranking.individual_scores_json || r.individual_scores || {};
    // Merge with legacy fixed fields as fallback
    const individualScores = { ...dynamicIndividual };
    const legacyFields = [
        'scope_facilities', 'scope_work', 'deliverables_quality',
        'total_price', 'price_breakdown', 'optionals_included', 'capex_opex_methodology',
        'schedule', 'resources_allocation', 'exceptions',
        'safety_studies', 'regulatory_compliance'
    ];
    legacyFields.forEach(f => {
        if (!(f in individualScores) || !individualScores[f]) {
            individualScores[f] = r.ranking[f + '_score'] || r.individual_scores?.[f] || 0;
        }
    });

    return {
        position: idx + 1,
        provider_name: r.ranking.provider_name,
        overall_score: r.ranking.overall_score,
        compliance_percentage: r.ranking.compliance_percentage,
        scores,
        individual_scores: individualScores,
        evaluation: {
            strengths: r.details.strengths || [],
            weaknesses: r.details.weaknesses || [],
            recommendations: r.details.recommendations || [],
            summary: r.details.summary || ''
        }
    };
});

const configType = sortedRankings[0]?.config_type || 'default';

const stats = {
    total_providers: finalRanking.length,
    average_score: finalRanking.reduce((sum, r) => sum + (r.overall_score || 0), 0) / (finalRanking.length || 1),
    top_performer: finalRanking[0]?.provider_name || 'N/A',
    score_range: {
        min: Math.min(...finalRanking.map(r => r.overall_score || 0)),
        max: Math.max(...finalRanking.map(r => r.overall_score || 0))
    },
    evaluation_date: new Date().toISOString(),
    config_type: configType
};

console.log(`Final Ranking Generated (${configType} config):`);
finalRanking.forEach(r => {
    console.log(`   ${r.position}. ${r.provider_name}: ${r.overall_score} (${r.compliance_percentage}%)`);
});

return {
    json: {
        success: true,
        ranking: finalRanking,
        statistics: stats,
        message: `Scoring completed for ${finalRanking.length} providers using ${configType} criteria configuration`
    }
};

// =============================================
// NODO: Preparar Texto (Agregador de Páginas)
// =============================================

const items = $input.all();
if (!items || items.length === 0) {
    return { json: { error: "No input items received" } };
}

// Recuperar metadatos del inicio del flujo (Generate IDs1)
let metadata = {};
try {
    metadata = $('Generate IDs1').first().json;
} catch (e) {
    console.log("⚠️ No se pudo recuperar metadata de Generate IDs1");
}

// 1. Agregado de Texto Completo (todas las páginas)
// Intentamos md_content primero (mejor para LLMs), luego text
let fullText = items.map(item => item.json.md_content || item.json.text || "").join('\n\n').trim();

if (!fullText) {
    console.log("⚠️ Advertencia: No se extrajo texto del documento.");
}

// 2. Limpieza básica
fullText = fullText.replace(/\x00/g, ''); // Eliminar caracteres nulos

// 3. Preparar fragmento para clasificación (máx 40k chars para no saturar context)
const textForClassification = fullText.substring(0, 40000);

console.log(`✅ Texto preparado: ${items.length} páginas aggregadas. Chars: ${fullText.length}`);

return {
    json: {
        rfq_project_id: metadata.rfq_project_id || "unknown",
        rfq_document_id: metadata.rfq_document_id || "unknown",
        project_name: metadata.project_name || "Sin Nombre",
        file_title: metadata.file_title || "document.pdf",
        text: fullText, 
        text_for_classification: textForClassification || "SIN TEXTO DETECTADO",
        pages_count: items.length
    }
};

