// =============================================
// NODO: Dividir en Trozos (Chunking)
// =============================================

const input = $input.first().json;
const fullText = input.texto_rfq || "";

// OPTIMIZADO: Chunks más pequeños para procesamiento más rápido
// ~6000 caracteres (aprox 1.5k-2k tokens) = respuestas en 10-15 seg
const chunkSize = 6000;
const overlap = 400;
const chunks = [];

if (fullText.length <= chunkSize) {
    chunks.push({
        ...input,
        texto_chunk: fullText,
        chunk_index: 1,
        total_chunks: 1
    });
} else {
    let start = 0;
    while (start < fullText.length) {
        let end = start + chunkSize;
        let chunk = fullText.substring(start, end);

        chunks.push({
            ...input,
            texto_chunk: chunk,
            chunk_index: chunks.length + 1
        });

        start += (chunkSize - overlap);
        if (start >= fullText.length) break;
    }
    chunks.forEach(c => c.total_chunks = chunks.length);
}

return chunks;

const items = $input.all();

return items.map(item => ({
    json: {
        ...item.json, // Mantiene todos los datos originales (incluido tipo_evaluacion)

        // Aquí está la corrección: Usamos dos puntos, no el igual.
        reiniciar_bucle: true 
    }
}));

