// =============================================
// NODO: Parsear y Preparar Update (SIN quoted_value)
// =============================================

// 1. Obtener TODOS los items del LLM Chain
const allItems = $input.all();
console.log(`📥 Procesando ${allItems.length} respuestas del LLM`);

// 2. Obtener la lista de requisitos VÁLIDOS (para validar FK)
let validRequirements = [];
try {
  const getRowsData = $('Get many rows1').all();
  validRequirements = getRowsData.map(item => item.json.id);
  console.log(`✅ Lista de requisitos válidos: ${validRequirements.length} items`);
} catch(e) {
  console.error('❌ Error obteniendo lista de requisitos válidos:', e.message);
  return [];
}

// 3. Obtener el provider name (una sola vez, es el mismo para todos)
let providerName = "OTROS";
try {
  providerName = $('Expandir por Tipo de Evaluación').first().json.proveedor_detectado || "OTROS";
} catch(e) {
  console.warn('⚠️ Error obteniendo provider, usando default:', e.message);
}

// 4. Procesar CADA respuesta del LLM
const resultados = [];

for (let i = 0; i < allItems.length; i++) {
  try {
    const item = allItems[i];
    const itemData = item.json || item;

    let rawText = itemData.text || itemData.output || "";

    if (!rawText || typeof rawText !== 'string') {
      console.warn(`⚠️ Item ${i}: No se encontró campo 'text' o 'output'`);
      continue;
    }

    let cleanText = rawText
      .replace(/```json/g, '')
      .replace(/```/g, '')
      .trim();

    let parsedData = null;

    try {
      const start = cleanText.indexOf('[');
      const end = cleanText.lastIndexOf(']');

      if (start >= 0 && end >= 0 && end > start) {
        const jsonString = cleanText.substring(start, end + 1);
        parsedData = JSON.parse(jsonString);
      } else {
        parsedData = JSON.parse(cleanText);
      }
    } catch (parseError) {
      console.error(`❌ Error parseando JSON del item ${i}:`, parseError.message);
      console.error(`Texto recibido:`, cleanText.substring(0, 200));
      continue;
    }

    const outputArray = Array.isArray(parsedData) ? parsedData : [parsedData];

    for (let j = 0; j < outputArray.length; j++) {
      const outputItem = outputArray[j];

      if (!outputItem || typeof outputItem !== 'object') {
        console.warn(`⚠️ Item ${i}.${j}: Elemento de salida no es objeto`);
        continue;
      }

      const cumple = outputItem?.cumple || "ERROR ANALISIS";
      const gap = outputItem?.gap_analysis || "Check logs";
      const score = outputItem?.score !== undefined ? outputItem.score : 0;
      const requirementId = outputItem?.id || null;

      const validatedScore = Math.max(0, Math.min(10, parseInt(score) || 0));

      if (!requirementId) {
        console.error(`❌ Item ${i}.${j}: No se encontró ID del requisito en la respuesta del LLM`);
        continue;
      }

      if (!validRequirements.includes(requirementId)) {
        console.error(`❌ Item ${i}.${j}: El requirement_id ${requirementId} NO existe en rfq_items_master. Saltando...`);
        continue;
      }

      console.log(`✅ Item ${i}.${j}: Requisito ${requirementId} - ${cumple} (${validatedScore}/10)`);

      resultados.push({
        json: {
          requirement_id: requirementId,
          provider_name: providerName,
          evaluation_value: cumple,
          comment: gap,
          score: validatedScore
        }
      });
    }

  } catch (error) {
    console.error(`❌ Error procesando item ${i}:`, error.message);
    continue;
  }
}

console.log(`✅ Procesados ${resultados.length} de ${allItems.length} items exitosamente`);

return resultados;

// =============================================
// NODO: Validar Contenido PDF (FIX PAGINACIÓN)
// =============================================

const input = $input.first().json;

// Variables de inicialización
let pages = [];
let totalChars = 0;
let hasTablesDetected = false;

// --- 1. PROCESAMIENTO DEL TEXTO ---

// CASO A: Viene desglosado por páginas (Array)
if (input.pages && Array.isArray(input.pages) && input.pages.length > 0) {
  pages = input.pages;
  pages.forEach(page => {
    const text = page.text || page.pageContent || "";
    totalChars += text.length;
    // Detección simple de tablas
    if ((text.match(/\s{4,}/g) || []).length > 5) hasTablesDetected = true;
  });

// CASO B: Viene todo el texto junto (String) - ESTE ES TU CASO ACTUAL
} else if (input.text && typeof input.text === 'string') {
  const text = input.text;
  totalChars = text.length;
  // Creamos una "página única" contenedora para no romper el flujo
  pages = [{ text: text, pageNumber: 1 }];

  // Detectar tablas en el texto completo
  if ((text.match(/\s{4,}/g) || []).length > 20) hasTablesDetected = true;

} else {
  // CASO C: Vacío
  pages = [];
}

// --- 2. CÁLCULO DE PÁGINAS REALES (FIX CRÍTICO) ---

// Intentamos leer el metadato 'numpages' que se ve en tu captura
let realPageCount = input.numpages || input.numPages || input.numberOfPages || 0;

// Si no existe el metadato, usamos la longitud del array (fallback)
if (!realPageCount || realPageCount === 0) {
    realPageCount = pages.length;
}

// Si sigue siendo 1 pero el texto es enorme (>10k chars), estimamos
if (realPageCount <= 1 && totalChars > 10000) {
    // Estimación conservadora: 3000 caracteres por página técnica
    realPageCount = Math.ceil(totalChars / 3000);
}

// Evitar división por cero
realPageCount = Math.max(1, realPageCount);


// --- 3. MÉTRICAS FINALES ---

const avgCharsPerPage = totalChars / realPageCount;

// CRITERIO: ¿Necesita OCR?
// Si hay texto (< 100 chars/pag) es probable que sea escaneado.
const needsOCR = avgCharsPerPage < 100; 

// Metadatos
let fileId = "unknown";
let fileTitle = "unknown";
try {
  const metaNode = $('Set File ID').first().json;
  fileId = metaNode.file_id;
  fileTitle = metaNode.file_title;
} catch (e) {}

// --- LOGGING ---
console.log(`📄 Análisis: ${fileTitle}`);
console.log(`   ├─ Páginas (Metadato): ${input.numpages}`); 
console.log(`   ├─ Páginas (Usadas): ${realPageCount}`);
console.log(`   ├─ Caracteres Totales: ${totalChars}`);
console.log(`   ├─ Promedio/Pág: ${Math.round(avgCharsPerPage)}`);

return {
  json: {
    // Métricas corregidas
    totalPages: realPageCount, // Ahora dirá 235
    totalChars: totalChars,
    avgCharsPerPage: Math.round(avgCharsPerPage), // Ahora será un número razonable (~2800)
    hasTables: hasTablesDetected,
    needsOCR: needsOCR,

    // Contenido
    pages: pages, 

    // Metadatos
    fileId: fileId,
    fileName: fileTitle
  }
};

// =============================================
// NODO: Aggregate OCR Pages (CORREGIDO)
// =============================================

const items = $input.all();
let fullText = "";
let pagesArray = [];

// OPCIÓN A: Si Tesseract devuelve múltiples items (1 por página)
if (items.length > 1) {
  items.forEach((item, idx) => {
    const pageText = item.json.text || "";
    fullText += pageText + "\n\n";

    // Construimos array compatible con vectorstore
    pagesArray.push({
      text: pageText,
      pageNumber: idx + 1
    });
  });
}
// OPCIÓN B: Si Tesseract devuelve un solo item con todo el texto
else if (items.length === 1) {
  fullText = items[0].json.text || "";
  pagesArray = [{
    text: fullText,
    pageNumber: 1
  }];
}

// RECUPERAR METADATOS ORIGINALES (CRÍTICO)
let fileId, fileName, proveedor;
try {
  fileId = $('Set File ID').first().json.file_id;
  fileName = $('Set File ID').first().json.file_title;
  proveedor = $('Set File ID').first().json.proveedor || "OTROS";
} catch (e) {
  console.error("⚠️ No se pudieron recuperar metadatos:", e.message);
  fileId = "unknown";
  fileName = "unknown";
  proveedor = "OTROS";
}

console.log(`🔄 OCR Agregado: ${items.length} páginas → ${fullText.length} caracteres`);

// ESTRUCTURA COMPATIBLE CON VECTORSTORE
return {
  json: {
    pages: pagesArray, // ← Array de páginas (requerido por Default Data Loader)
    text: fullText, // ← Texto completo (backup)

    // Metadatos críticos
    file_id: fileId,
    file_title: fileName,
    proveedor: proveedor,

    // Marca de origen
    origen_datos: "OCR_TESSERACT",
    ocr_pages_processed: items.length,
    ocr_total_chars: fullText.length
  }
};

// =============================================
// NODO: Preparar Texto de Muestra (STRICT LIMIT)
// =============================================

const items = $input.all();

return items.map(item => {
    let texto_muestra = "SIN TEXTO";

    try {
        if (item.json.pages && Array.isArray(item.json.pages) && item.json.pages.length > 0) {
            // Unimos las 2 primeras páginas pero limitamos el total a 3000 caracteres
            let rawJoin = item.json.pages.slice(0, 2).map(p => p.text || "").join("\n\n");
            texto_muestra = rawJoin.substring(0, 3000);
        } else {
            // Fallback extremadamente corto para texto plano
            texto_muestra = (item.json.text || "").substring(0, 500);
        }
    } catch (e) {
        console.error("Error al procesar páginas:", e);
    }

    return {
        json: {
            ...item.json,
            texto_muestra: texto_muestra
        }
    };
});

// =============================================
// NODO: Format for Vectorstore (v3 - DIRECTO DEL USUARIO)
// SIN clasificador ni validador LLM
// =============================================

// 1. RECUPERAR CONTENIDO
let fullText = "";
let totalPages = 1;

try {
    const mergeData = $('Merge1').first().json;
    if (mergeData.pages && mergeData.pages[0]) {
        fullText = mergeData.pages.slice(0, 2).map(p => p.text || "").join("\n\n");
    } else {
        fullText = mergeData.text || "";
    }
    totalPages = mergeData.totalPages || 1;
} catch (e) {
    console.log("❌ Error leyendo de Merge1");
}

// 2. USAR DIRECTAMENTE DATOS DEL USUARIO (Set File ID)
let fileId = "unknown";
let fileTitle = "unknown";
let proveedor = "OTROS";
let tipoEval = ["Technical Evaluation"];
let proyecto = "";
let projectId = null;

try {
    const fileNode = $('Set File ID').first().json;
    fileId = fileNode.file_id || "unknown";
    fileTitle = fileNode.file_title || "unknown";
    proveedor = fileNode.proveedor || "OTROS";
    proyecto = fileNode.proyect_name || "";
    projectId = fileNode.project_id || null;

    if (fileNode.evaluation && Array.isArray(fileNode.evaluation) && fileNode.evaluation.length > 0) {
        tipoEval = fileNode.evaluation;
    } else if (fileNode.evaluation && typeof fileNode.evaluation === 'string') {
        tipoEval = [fileNode.evaluation];
    }
} catch(e) {
    console.log("❌ Error leyendo Set File ID");
}

// 3. GENERAR SALIDA
return {
    json: {
        text: fullText,
        metadata: {
            file_id: fileId,
            file_title: fileTitle,
            proveedor: proveedor,
            tipo_evaluacion: tipoEval,
            project_name: proyecto,
            project_id: projectId,
            processed_at: new Date().toISOString(),
            total_pages: totalPages
        }
    }
};

// =============================================
// NODO: Expandir por Tipo de Evaluación (v3 - DIRECTO DEL USUARIO)
// SIN clasificador ni validador LLM - usa datos del usuario directamente
// =============================================

// 1. RECUPERAR TEXTO Y CONTENIDO BASE
let fullText = "";
let totalPages = 1;
try {
    const mergeData = $('Merge1').first().json;
    fullText = (mergeData.pages && mergeData.pages[0]) 
        ? mergeData.pages.slice(0, 2).map(p => p.text || "").join("\n\n") 
        : (mergeData.text || "");
    totalPages = mergeData.totalPages || 1;
} catch (e) {
    console.error("❌ Error leyendo Merge1");
}

// 2. USAR DIRECTAMENTE DATOS DEL USUARIO (Set File ID)
let fileId = "unknown";
let proveedor = "OTROS";
let tipoEval = ["Technical Evaluation"];
let projectName = "proyecto_sin_especificar";

try {
    const fileData = $('Set File ID').first().json;
    fileId = fileData.file_id || "unknown";
    proveedor = fileData.proveedor || "OTROS";
    projectName = fileData.proyect_name || "proyecto_sin_especificar";

    // Tipos de evaluación del usuario
    if (fileData.evaluation && Array.isArray(fileData.evaluation) && fileData.evaluation.length > 0) {
        tipoEval = fileData.evaluation;
    } else if (fileData.evaluation && typeof fileData.evaluation === 'string') {
        tipoEval = [fileData.evaluation];
    }

    console.log(`✅ Usando datos del usuario directamente:`);
    console.log(`   Proveedor: ${proveedor}`);
    console.log(`   Proyecto: ${projectName}`);
    console.log(`   Tipos: ${JSON.stringify(tipoEval)}`);
} catch (e) {
    console.error("❌ Error leyendo Set File ID:", e.message);
}

// 3. NORMALIZAR TIPOS
if (!Array.isArray(tipoEval)) tipoEval = [tipoEval];
tipoEval = tipoEval.filter(t => t && t.trim() !== "");
if (tipoEval.length === 0) tipoEval = ["Technical Evaluation"];

// 4. GENERAR FILAS
return tipoEval.map((tipo, idx) => ({
