        }
    }
};

// =============================================
// NODO: Generate Stable ID + Detectar Modo (NOMBRES CORRECTOS)
// =============================================

function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16).padStart(16, '0').substring(0, 16);
}

const body = $json.body;

// CAMPOS EXISTENTES
const originalFileId = body.file_id || "unknown";
const fileTitle = body.file_title || "unknown.pdf";
const fileBinary = body.file_binary || "";
const fileUrl = body.file_url || "unknown";

// ✅ EXTRAER METADATA DEL USUARIO (NOMBRES CORRECTOS)
const metadata = body.metadata || {};
const rfqProjectId = metadata.proyect_name || null;        // ← proyect_name
const userProveedor = metadata.proveedor || null;          // ← proveedor
const userTipoEval = metadata.evaluation || null;          // ← evaluation (Array)

// ✅ DETERMINAR MODO DE OPERACIÓN
let modoOperacion = "CLASIFICADOR"; // Por defecto
let datosCompletos = false;

// Verificar si tiene datos suficientes para VALIDACIÓN
if (userProveedor && userTipoEval && Array.isArray(userTipoEval) && userTipoEval.length > 0) {
    modoOperacion = "VALIDADOR";
    datosCompletos = true;
} else if (userProveedor || (userTipoEval && Array.isArray(userTipoEval) && userTipoEval.length > 0)) {
    modoOperacion = "HIBRIDO"; // Tiene algunos datos pero no todos
}

// Generar ID estable del archivo (incluye project_id para evitar colisiones entre proyectos)
const projectId = body.project_id || metadata.project_id || '';
const normalizedTitle = fileTitle.toLowerCase().trim();
const stableId = simpleHash(normalizedTitle + '_' + projectId);

// LOGGING
console.log("=== ANÁLISIS DE METADATA ===");
console.log(`Modo Operación: ${modoOperacion}`);
console.log(`RFQ Project Name: ${rfqProjectId || 'No especificado'}`);
console.log(`Proveedor (Usuario): ${userProveedor || 'No especificado'}`);
console.log(`Evaluation (Usuario): ${JSON.stringify(userTipoEval) || 'No especificado'}`);
console.log(`Datos Completos: ${datosCompletos}`);
console.log(`Original ID: ${originalFileId}`);
console.log(`Nombre archivo: ${fileTitle}`);
console.log(`ID ESTABLE generado: ${stableId}`);

// RETORNO
return {
  json: {
    // Datos directos en raíz
    file_id: stableId,
    file_id_original: originalFileId,
    file_title: fileTitle,
    file_binary: fileBinary,
    file_url: fileUrl,

    rfq_project_id: rfqProjectId || "proyecto_sin_especificar",
    user_proveedor: userProveedor,
    user_tipo_evaluacion: userTipoEval,
    modo_operacion: modoOperacion,
    datos_usuario_completos: datosCompletos,

    // Mantener body completo
    body: {
      ...body,
      file_id: stableId,
      file_id_original: originalFileId,
      rfq_project_id: rfqProjectId || "proyecto_sin_especificar",
      user_proveedor: userProveedor,
      user_tipo_evaluacion: userTipoEval,
      modo_operacion: modoOperacion,
      datos_usuario_completos: datosCompletos
    }
  }
};

// =============================================
// NODO: Preparar Todo para LLM (SIN RAG, SIN FASES)
// Pasa TODOS los requisitos directamente al LLM
// =============================================

const items = $input.all();

console.log('📥 Requisitos recibidos:', items.length);

// Obtener texto completo de la oferta desde Loop Over Items1
let textoOfertaCompleto = "";
let evaluationType = "Technical Evaluation";

try {
    const loopData = $('Loop Over Items1').first().json;
    textoOfertaCompleto = loopData.texto_oferta_completo || "";
    evaluationType = loopData.tipo_evaluacion_actual || "Technical Evaluation";
    console.log('✅ Datos obtenidos de Loop Over Items1');
    console.log('   ├─ Tipo evaluación:', evaluationType);
    console.log('   └─ Texto oferta:', textoOfertaCompleto.length, 'chars');
} catch(e) {
    console.warn('⚠️ No se pudo obtener texto de oferta de Loop Over Items1:', e.message);
    // Fallback: intentar obtener de otro lugar
    try {
        const expandirData = $('Expandir por Tipo de Evaluación').first().json;
        textoOfertaCompleto = expandirData.texto_oferta_completo || "";
        evaluationType = expandirData.tipo_evaluacion_actual || "Technical Evaluation";
        console.log('✅ Fallback: Datos obtenidos de Expandir por Tipo de Evaluación');
    } catch(e2) {
        console.error('❌ No se pudo obtener texto de ningún nodo');
    }
}

// Formatear TODOS los requisitos (sin batching)
const todosLosRequisitos = items.map(item => ({
    id: item.json.id || "",
    requirement_text: item.json.requirement_text || item.json.text || "NO ESPECIFICADO",
    evaluation_type: item.json.evaluation_type || evaluationType
}));

console.log('📋 Preparación directa (sin RAG ni fases):');
console.log('   ├─ Total requisitos:', todosLosRequisitos.length);
console.log('   ├─ Tipo evaluación:', evaluationType);
console.log('   └─ Tamaño texto oferta:', textoOfertaCompleto.length, 'chars');

// Retornar UN SOLO item con TODOS los requisitos
return [{
    json: {
        evaluation_type: evaluationType,
        requisitos: todosLosRequisitos,
        total_requisitos: todosLosRequisitos.length,
        batch_index: 1,
        total_batches: 1,
        context: textoOfertaCompleto,
        texto_length: textoOfertaCompleto.length
    }
}];

