// =============================================
// NODO: Preparar Requisito + Texto Completo de Oferta
// Procesa TODOS los requisitos del loop
// =============================================

// 1. Obtener TODOS los requisitos del loop
const allItems = $input.all();
console.log(`üìã Procesando ${allItems.length} requisitos del loop`);

// 2. Obtener el texto completo de la oferta del nodo Merge1 (una sola vez)
let textoCompletoOferta = "";
let totalPages = 1;

try {
    const mergeData = $('Merge').first().json;

    // El Merge1 puede tener el texto en diferentes formatos
    if (mergeData.pages && Array.isArray(mergeData.pages) && mergeData.pages.length > 0) {
        // Si tiene p√°ginas, unimos todas las p√°ginas (texto completo)
        textoCompletoOferta = mergeData.pages.map(p => p.text || "").join("\n\n");
        totalPages = mergeData.pages.length;
    } else if (mergeData.text) {
        // Si tiene texto directo
        textoCompletoOferta = mergeData.text;
        totalPages = mergeData.totalPages || 1;
    } else {
        // Buscar cualquier propiedad que contenga texto largo
        for (const key in mergeData) {
            const value = mergeData[key];
            if (typeof value === 'string' && value.length > 100) {
                textoCompletoOferta = value;
                break;
            }
        }
    }

    console.log(`‚úÖ Texto de oferta recuperado: ${textoCompletoOferta.length} caracteres, ${totalPages} p√°ginas`);
} catch (e) {
    console.error("‚ùå Error obteniendo texto completo de Merge1:", e.message);
    textoCompletoOferta = "ERROR: No se pudo recuperar el texto completo de la oferta.";
}

// 3. Validar que tenemos datos m√≠nimos
if (!textoCompletoOferta || textoCompletoOferta.trim().length === 0) {
    console.warn("‚ö†Ô∏è El texto de la oferta est√° vac√≠o");
    textoCompletoOferta = "SIN TEXTO DISPONIBLE EN LA OFERTA.";
}

// 4. Construir array de requisitos para el LLM
const requisitos = allItems.map(item => {
    const currentItem = item.json || item;
    const requisitoId = currentItem.id || "";
    // El campo correcto de Supabase es 'requirement_text'
    const requisitoTexto = currentItem.requirement_text || currentItem.requisito_rfq || currentItem.text || currentItem.description || "NO ESPECIFICADO EN RFQ";

    console.log(`üìã Preparando requisito ID: ${requisitoId} - ${requisitoTexto.substring(0, 50)}...`);

    return {
        id: requisitoId,
        requirement_text: requisitoTexto,
        // Mantener otros campos del requisito por si acaso
        project_name: currentItem.project_name,
        evaluation_type: currentItem.evaluation_type,
        phase: currentItem.phase
    };
});

// 5. Retornar UN SOLO item con la lista de requisitos y el contexto completo
return [{
    json: {
        requisitos: requisitos,
        context: textoCompletoOferta,
        // Metadatos adicionales para debugging
        total_pages: totalPages,
        texto_length: textoCompletoOferta.length,
        total_requisitos: requisitos.length
    }
}];

// =============================================
// NODO: Generate Stable ID + Detectar Modo (NOMBRES CORRECTOS)
// =============================================

function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16).padStart(16, '0').substring(0, 16);
}

