// =============================================
// NODO: Expandir por Tipo de Evaluaci√≥n (v2 - Valida Metadatos)
// =============================================

// 1. RECUPERAR TEXTO Y CONTENIDO BASE
let fullText = "";
let totalPages = 1;
try {
    const mergeData = $('Merge1').first().json;
    fullText = (mergeData.pages && mergeData.pages[0]) 
        ? mergeData.pages.slice(0, 2).map(p => p.text || "").join("\n\n") 
        : (mergeData.text || "");
    totalPages = mergeData.totalPages || 1;
} catch (e) {
    console.error("‚ùå Error leyendo Merge1");
}

// 2. RECUPERAR DATOS VALIDADOS PRIORITARIAMENTE
let proveedor = "OTROS";
let tipoEval = ["Technical Evaluation"];
let projectName = "proyecto_sin_especificar";

try {
    let llmNode = null;
    try { llmNode = $('Validador de Ofertas').first().json; } catch(e) {}
    if (!llmNode) { try { llmNode = $('Clasificador de Tipo de Evaluacion y Proveedor').first().json; } catch(e) {} }

    if (llmNode) {
        const data = llmNode.output || llmNode;
        proveedor = data.proveedor_detectado || proveedor;
        tipoEval = data.tipos_detectados || tipoEval;
        // El proyecto tambi√©n puede venir del validador si se detect√≥
        if (data.proyecto_detectado) projectName = data.proyecto_detectado;
    }
} catch (e) {
    console.log("‚ö†Ô∏è Fall√≥ lectura de LLM, intentando fallbacks...");
}

// 3. FALLBACK A SET FILE ID (Si el LLM no dio datos)
let fileId = "unknown";
try {
    const fileData = $('Set File ID').first().json;
    fileId = fileData.file_id || "unknown";
    if (proveedor === "OTROS") proveedor = fileData.proveedor || "OTROS";
    if (projectName === "proyecto_sin_especificar") projectName = fileData.proyect_name || "proyecto_sin_especificar";
} catch (e) {}

// 4. NORMALIZAR TIPOS
if (!Array.isArray(tipoEval)) tipoEval = [tipoEval];
tipoEval = tipoEval.filter(t => t && t.trim() !== "");
if (tipoEval.length === 0) tipoEval = ["Technical Evaluation"];

// 5. GENERAR FILAS
return tipoEval.map((tipo, idx) => ({
    json: {
        tipo_evaluacion_actual: tipo,
        proveedor_detectado: proveedor,
        proyecto_nombre: projectName,
        texto_oferta_completo: fullText,
        indice_actual: idx + 1,
        total_tipos: tipoEval.length,
        file_id: fileId
    }
}));

// =============================================
// NODO: Format for Vectorstore (FINAL v2)
// =============================================

// 1. RECUPERAR CONTENIDO (Instrucci√≥n expl√≠cita)
let fullText = "";
let totalPages = 1;

try {
    const mergeData = $('Merge1').first().json;
    if (mergeData.pages && mergeData.pages[0]) {
        fullText = mergeData.pages.slice(0, 2).map(p => p.text || "").join("\n\n");
    } else {
        fullText = mergeData.text || "";
    }
    totalPages = mergeData.totalPages || 1;
} catch (e) {
    console.log("‚ùå Error leyendo de Merge1");
}

// 2. RECUPERAR CLASIFICACI√ìN O VALIDACI√ìN
let proveedor = "OTROS";
let tipoEval = ["Technical Evaluation"];
let proyecto = "";

try {
    // Intentamos leer del validador primero, si no, del clasificador
    let llmData;
    try {
        llmData = $('Validador de Ofertas').first().json;
    } catch (e) {
        llmData = $('Clasificador de Tipo de Evaluacion y Proveedor').first().json;
    }

    const output = llmData.output || llmData;

    if (output.proveedor_detectado) proveedor = output.proveedor_detectado;
    if (output.proyecto_detectado) proyecto = output.proyecto_detectado;

    if (output.tipos_detectados) {
        tipoEval = Array.isArray(output.tipos_detectados) 
            ? output.tipos_detectados 
            : [output.tipos_detectados];
    }
} catch (e) {
    console.log("‚ö†Ô∏è Error leyendo clasificaci√≥n/validaci√≥n LLM, usando defaults.");
}

// 3. RECUPERAR METADATOS DEL ARCHIVO Y FALLBACKS
let fileId = "unknown";
let fileTitle = "unknown";
try {
    const fileNode = $('Set File ID').first().json;
    fileId = fileNode.file_id;
    fileTitle = fileNode.file_title;
    if (!proyecto) proyecto = fileNode.proyect_name;
} catch(e) {}

// 4. GENERAR SALIDA
return {
    json: {
        text: fullText,
        metadata: {
            file_id: fileId,
            file_title: fileTitle,
            proveedor: proveedor,
            tipo_evaluacion: tipoEval,
            project_name: proyecto,
            processed_at: new Date().toISOString(),
            total_pages: totalPages
        }
    }
};

// =============================================
// NODO: Preparar Texto de Muestra (STRICT LIMIT)
// =============================================

const items = $input.all();

return items.map(item => {
    let texto_muestra = "SIN TEXTO";

    try {
        if (item.json.pages && Array.isArray(item.json.pages) && item.json.pages.length > 0) {
            // Unimos las 2 primeras p√°ginas pero limitamos el total a 3000 caracteres
            let rawJoin = item.json.pages.slice(0, 2).map(p => p.text || "").join("\n\n");
            texto_muestra = rawJoin.substring(0, 3000);
        } else {
            // Fallback extremadamente corto para texto plano
            texto_muestra = (item.json.text || "").substring(0, 500);
        }
    } catch (e) {
        console.error("Error al procesar p√°ginas:", e);
    }

    return {
        json: {
            ...item.json,
            texto_muestra: texto_muestra
        }
    };
});

// =============================================
// NODO: Aggregate OCR Pages (CORREGIDO)
// =============================================

const items = $input.all();
let fullText = "";
let pagesArray = [];

// OPCI√ìN A: Si Tesseract devuelve m√∫ltiples items (1 por p√°gina)
if (items.length > 1) {
  items.forEach((item, idx) => {
    const pageText = item.json.text || "";
    fullText += pageText + "\n\n";

    // Construimos array compatible con vectorstore
    pagesArray.push({
      text: pageText,
      pageNumber: idx + 1
    });
  });
}
// OPCI√ìN B: Si Tesseract devuelve un solo item con todo el texto
else if (items.length === 1) {
  fullText = items[0].json.text || "";
  pagesArray = [{
    text: fullText,
    pageNumber: 1
  }];
}

// RECUPERAR METADATOS ORIGINALES (CR√çTICO)
let fileId, fileName, proveedor;
try {
  fileId = $('Set File ID').first().json.file_id;
  fileName = $('Set File ID').first().json.file_title;
  proveedor = $('Set File ID').first().json.proveedor || "OTROS";
} catch (e) {
  console.error("‚ö†Ô∏è No se pudieron recuperar metadatos:", e.message);
  fileId = "unknown";
  fileName = "unknown";
  proveedor = "OTROS";
}

console.log(`üîÑ OCR Agregado: ${items.length} p√°ginas ‚Üí ${fullText.length} caracteres`);

// ESTRUCTURA COMPATIBLE CON VECTORSTORE
return {
  json: {
    pages: pagesArray, // ‚Üê Array de p√°ginas (requerido por Default Data Loader)
    text: fullText, // ‚Üê Texto completo (backup)

    // Metadatos cr√≠ticos
    file_id: fileId,
