console.log(`   ‚îú‚îÄ P√°ginas (Metadato): ${input.numpages}`); 
console.log(`   ‚îú‚îÄ P√°ginas (Usadas): ${realPageCount}`);
console.log(`   ‚îú‚îÄ Caracteres Totales: ${totalChars}`);
console.log(`   ‚îú‚îÄ Promedio/P√°g: ${Math.round(avgCharsPerPage)}`);

return {
  json: {
    // M√©tricas corregidas
    totalPages: realPageCount, // Ahora dir√° 235
    totalChars: totalChars,
