const input = $input.first().json;
let rawText = "";

// Recuperar metadatos PRIORITARIAMENTE
let projectId = "unknown";
let documentId = "unknown";
let fileTitle = "unknown";
let fullText = "";

try {
    const textNode = $('Preparar Texto1').first().json;
    projectId = textNode.rfq_project_id;
    documentId = textNode.rfq_document_id || "unknown";
    fileTitle = textNode.file_title;
    fullText = textNode.text;
} catch(e) {
    console.log("⚠️ Error recuperando metadatos de Preparar Texto1");
}

// Extraer texto de forma segura
if (typeof input === 'string') rawText = input;
else if (input.text && typeof input.text === 'string') rawText = input.text;
else if (input.output && typeof input.output === 'string') rawText = input.output;
else if (input.content && typeof input.content === 'string') rawText = input.content;
else if (input.text || input.output || input.content) rawText = JSON.stringify(input.text || input.output || input.content);
else rawText = JSON.stringify(input);

// Asegurar que sea string para evitar errores con toLowerCase/replace
rawText = String(rawText || "");

let output = {};
try {
    // 1. Limpiar bloques markdown
    const clean = rawText.replace(/```json/gi, '').replace(/```/g, '').trim();

    // 2. Intentar parsear JSON
    const start = clean.indexOf('{');
    const end = clean.lastIndexOf('}');

    if (start !== -1 && end !== -1) {
        output = JSON.parse(clean.substring(start, end + 1));
    } else {
        output = JSON.parse(clean);
    }
} catch (e) {
    console.log("⚠️ Parsing fallback: ", e.message);
    // Fallback: Inferir del texto si falló el JSON
    output = { 
        tipos_detectados: [], 
        nombre_proyecto: "H2 Plant in La Zaida, Zaragoza (Spain)" 
    };

    const searchStr = rawText.toLowerCase();
    const titleStr = (fileTitle || "").toLowerCase();

    // Regla de Título (CRÍTICA)
    if (titleStr.includes("rfq") || titleStr.includes("request for quotation")) {
        output.tipos_detectados.push("Technical Evaluation", "Economical Evaluation");
    } else {
        if (searchStr.includes("technical")) output.tipos_detectados.push("Technical Evaluation");
        if (searchStr.includes("economical")) output.tipos_detectados.push("Economical Evaluation");
        if (searchStr.includes("pre-feed")) output.tipos_detectados.push("Pre-FEED Deliverables");
        if (searchStr.includes("feed")) output.tipos_detectados.push("FEED Deliverables");
    }

    // Eliminar duplicados si los hubiera y asegurar al menos uno
    output.tipos_detectados = [...new Set(output.tipos_detectados)];
    if (output.tipos_detectados.length === 0) output.tipos_detectados = ["Technical Evaluation"];
}

let tipos = output.tipos_detectados || ["Technical Evaluation"];
const realProjectName = output.nombre_proyecto || "H2 Plant in La Zaida, Zaragoza (Spain)";

return tipos.map((tipo, idx) => ({
    json: {
        tipo_evaluacion: tipo,
        rfq_project_id: projectId,
        rfq_document_id: documentId,
        nombre_proyecto: realProjectName,
        file_title: fileTitle,
        texto_rfq: fullText,
        indice: idx + 1,
        total_tipos: tipos.length
    }
}));

// =============================================
// NODO: Parsear Final (Con Nombre Proyecto)
// =============================================
const inputJson = $input.first().json;
// Asegurar que sea string
let llmOutput = "";
if (typeof inputJson === 'string') llmOutput = inputJson;
else if (inputJson.text && typeof inputJson.text === 'string') llmOutput = inputJson.text;
else if (inputJson.output && typeof inputJson.output === 'string') llmOutput = inputJson.output;
else if (inputJson.content && typeof inputJson.content === 'string') llmOutput = inputJson.content;
else llmOutput = JSON.stringify(inputJson.text || inputJson.output || inputJson.content || inputJson);

llmOutput = String(llmOutput || "[]");

// 1. Recuperar Contexto del Loop
let projectId = "unknown";
let documentId = "unknown";
let tipoEvaluacion = "Unknown";
let nombreProyecto = "Sin Nombre";

try {
    const loopData = $('Loop por Tipo1').first().json;
    projectId = loopData.rfq_project_id;
    documentId = loopData.rfq_document_id;
    tipoEvaluacion = loopData.tipo_evaluacion;
    nombreProyecto = loopData.nombre_proyecto || "Sin Nombre";
} catch(e) {}

// 2. Lógica de Parsing
let items = [];

try {
    let clean = llmOutput.replace(/```json/gi, '').replace(/```/g, '').trim();
    const start = clean.indexOf('[');
    const end = clean.lastIndexOf(']');
    if (start !== -1 && end !== -1) {
        items = JSON.parse(clean.substring(start, end + 1));
    }
} catch(e) {}

if (items.length === 0) {
    const lines = llmOutput.split('\n');
    let currentPhase = "General";
    for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;
        if (/^\d+\./.test(line) && !line.includes('Estimate')) {
            const text = line.replace(/^\d+\.\s*/, '').trim();
            if (i+1 < lines.length && (lines[i+1].trim().startsWith('-') || lines[i+1].trim().startsWith('•'))) {
                currentPhase = text;
            } else {
                 items.push({ fase: "General", requisito_rfq: text });
            }
        }
        else if (line.startsWith('-') || line.startsWith('•')) {
            items.push({ fase: currentPhase, requisito_rfq: line.replace(/^[-•]\s*/, '').trim() });
        }
    }
}

function generateId(str) {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
        hash = ((hash << 5) - hash) + str.charCodeAt(i);
        hash = hash & hash;
    }
    return 'item_' + Math.abs(hash).toString(16).padStart(12, '0');
}

return items.map(item => ({
    json: {
        rfq_project_id: projectId,
        project_name: nombreProyecto,
        rfq_document_id: documentId,
        evaluation: tipoEvaluacion,
        fase: item.fase || "General",
        requisito_rfq: item.requisito_rfq || "Sin Nombre"
    }
}));

// =============================================
// NODO: Agregar Resultados del Tipo Actual
// =============================================

const items = $input.all();

// Contar items insertados
const totalInserted = items.length;

// Recuperar info del tipo actual
let tipoActual = "unknown";
try {
    tipoActual = $('Loop por Tipo1').first().json.tipo_evaluacion;
} catch(e) {}

console.log(`✅ Tipo ${tipoActual}: ${totalInserted} items insertados`);

return {
    json: {
        tipo_procesado: tipoActual,
        items_insertados: totalInserted,
        continuar_loop: true
    }
};

// =============================================
// NODO: Resumen Final
// =============================================

// Recuperar datos del proyecto
let projectId = "unknown";
let fileTitle = "unknown";
let tiposDetectados = [];

try {
    const projectNode = $('Generate IDs1').first().json;
    projectId = projectNode.rfq_project_id;
    fileTitle = projectNode.file_title;
} catch(e) {}

try {
    const clasificador = $('Clasificador de Tipos1').first().json;
    const output = clasificador.output || clasificador;
    tiposDetectados = output.tipos_detectados || [];
} catch(e) {}

console.log("========================================");
console.log("✅ PROCESAMIENTO RFQ COMPLETADO");
console.log(`   Proyecto: ${projectId}`);
console.log(`   Archivo: ${fileTitle}`);
console.log(`   Tipos procesados: ${tiposDetectados.length}`);
console.log("========================================");

return {
    json: {
        success: true,
        rfq_project_id: projectId,
        file_title: fileTitle,
        tipos_procesados: tiposDetectados,
        mensaje: `RFQ procesada correctamente. ${tiposDetectados.length} tipo(s) de evaluación detectados y poblados.`
    }
};

// =============================================
// NODO: Base64 a Binary
// =============================================

// Recuperar metadatos del inicio del flujo (Generate IDs1)
let inputData = {};
try {
    inputData = $('Generate IDs1').first().json;
} catch (e) {
    inputData = $json; // Fallback
}

const base64Data = inputData.file_binary;

if (!base64Data || base64Data === "") {
    throw new Error("❌ CRÍTICO: 'file_binary' está vacío");
}

const binaryData = Buffer.from(base64Data, 'base64');

console.log(`✅ PDF convertido: ${binaryData.length} bytes`);

return {
    json: {
        rfq_project_id: inputData.rfq_project_id || "unknown",
        rfq_document_id: inputData.rfq_document_id || "unknown",
        project_name: inputData.project_name || "Sin Nombre",
        file_title: inputData.file_title || "document.pdf"
    },
    binary: {
        data: {
            data: binaryData.toString('base64'),
            mimeType: 'application/pdf',
            fileName: inputData.file_title || 'rfq.pdf',
            fileSize: binaryData.length
        }
    }
};

// =============================================
// NODO: Generate IDs (MEJORADO)
// =============================================
// Reemplaza el nodo "Generate Project ID"
// Este nodo genera IDs estables y únicos

function stableHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16).padStart(12, '0').substring(0, 12);
}

const body = $json.body || $json;
const fileTitle = body.file_title || "unknown.pdf";
const fileBinary = body.file_binary || "";

// NUEVO: Extraer project_name del webhook o usar file_title
const projectName = body.project_name || fileTitle.replace(/\.(pdf|docx)$/i, '');

// 1. PROJECT_ID (basado solo en nombre del proyecto, sin timestamp)
//    → Mismo proyecto = mismo ID, aunque sean diferentes documentos
const projectId = 'proj_' + stableHash(projectName.toLowerCase().trim());

// 2. DOCUMENT_ID (único por archivo, estable entre subidas)
//    → Mismo archivo = mismo ID, incluso si se sube múltiples veces
const documentId = 'doc_' + stableHash(fileTitle + fileBinary.substring(0, 1000));

console.log("=== GENERACIÓN DE IDs ===");
console.log(`Project Name: ${projectName}`);
console.log(`Project ID: ${projectId}`);
console.log(`Document ID: ${documentId}`);
console.log(`File Title: ${fileTitle}`);

return {
  json: {
    rfq_project_id: projectId,      // Compartido entre docs del mismo proyecto
    rfq_document_id: documentId,    // Único por documento
    project_name: projectName,      // Nombre legible del proyecto
    file_title: fileTitle,
    file_binary: fileBinary
  }
};

// =============================================
// NODO: Parsear Deliverables (ROBUST FALLBACK)
// =============================================

const inputJson = $input.first().json;

// 1. RECOGIDA DE DATOS RAW
let rawOutput = "";
if (inputJson.output && typeof inputJson.output === 'string') rawOutput = inputJson.output;
else if (inputJson.text && typeof inputJson.text === 'string') rawOutput = inputJson.text;
else if (inputJson.content && typeof inputJson.content === 'string') rawOutput = inputJson.content;
else if (typeof inputJson === 'string') rawOutput = inputJson;

// 2. INTENTO A: EXTRAER JSON
let items = [];
try {
    const clean = rawOutput.replace(/```json/gi, '').replace(/```/g, '').trim();
    const start = clean.indexOf('[');
    const end = clean.lastIndexOf(']');
    if (start !== -1 && end !== -1) {
        items = JSON.parse(clean.substring(start, end + 1));
    }
} catch(e) {}

// 3. INTENTO B: FALLBACK DE LISTA
if (items.length === 0 && rawOutput) {
    const lines = rawOutput.split('\n');
    let currentPhase = "General";
    lines.forEach(line => {
        const trimmed = line.trim();
        if (!trimmed) return;
        const numberMatch = trimmed.match(/^\d+\.\s*(.*)/);
        const bulletMatch = trimmed.match(/^[-•*]\s*(.*)/);
        if (numberMatch) {
            items.push({ fase: currentPhase, requisito_rfq: numberMatch[1] });
        } else if (bulletMatch) {
             items.push({ fase: currentPhase, requisito_rfq: bulletMatch[1] });
        }
    });
}

// 4. HELPER: GENERAR ID ESTABLE
function generateId(str) {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
        hash = ((hash << 5) - hash) + str.charCodeAt(i);
        hash = hash & hash;
    }
    return 'item_' + Math.abs(hash).toString(16).padStart(12, '0');
}

// 5. NORMALIZAR Y RETORNAR
let loopCtx = {};
try {
   const outerLoop = $('Loop por Tipo1').first().json;
   loopCtx = {
       rfq_project_id: outerLoop.rfq_project_id,
       rfq_document_id: outerLoop.rfq_document_id,
       project_name: outerLoop.nombre_proyecto,
       tipo_evaluacion: outerLoop.tipo_evaluacion
   };
} catch(e){}

return items.map(item => {
    const phase = item.fase || "General";
    const req = item.requisito_rfq || "Sin Nombre";
    return {
        json: {
            rfq_project_id: loopCtx.rfq_project_id || "unknown",
            rfq_document_id: loopCtx.rfq_document_id || "unknown",
            project_name: loopCtx.project_name || "Unknown",
            evaluation: loopCtx.tipo_evaluacion || "Deliverables",
            fase: phase,
            requisito_rfq: req
        }
    };
});

