// =============================================
// NODO: Expandir por Tipo (CORREGIDO)
// =============================================

// 1. Recuperar metadatos de nodos anteriores
let projectId = "unknown";
let documentId = "unknown";
let fileTitle = "unknown";
let fullText = "";
let metadataProjectName = "";

try {
    const textNode = $('Preparar Texto1').first().json;
    projectId = textNode.rfq_project_id || "unknown";
    documentId = textNode.rfq_document_id || "unknown";
    fileTitle = textNode.file_title || "unknown";
    fullText = textNode.text || "";
    metadataProjectName = textNode.project_name || ""; 
} catch(e) {
    console.log("‚ö†Ô∏è No se pudieron leer metadatos de Preparar Texto1");
}

// 2. Obtener los tipos DIRECTAMENTE del Clasificador (no del input actual)
let tipos = [];
let nombreProyecto = "";

try {
    const clasificadorData = $('Clasificador de Tipos1').first().json;

    // El clasificador puede devolver el output de diferentes formas
    let output = clasificadorData.output || clasificadorData;

    // Intentar parsear si es string
    if (typeof output === 'string') {
        try {
            const clean = output.replace(/```json/gi, '').replace(/```/g, '').trim();
            const start = clean.indexOf('{');
            const end = clean.lastIndexOf('}');
            if (start !== -1 && end !== -1) {
                output = JSON.parse(clean.substring(start, end + 1));
            } else {
                output = JSON.parse(clean);
            }
        } catch(e) {
            console.log("‚ö†Ô∏è Error parseando output del clasificador");
        }
    }

    // Extraer tipos y nombre del proyecto
    tipos = output.tipos_detectados || [];
    nombreProyecto = output.nombre_proyecto || "";

    console.log(`‚úÖ Tipos detectados por IA: ${JSON.stringify(tipos)}`);
} catch(e) {
    console.log("‚ö†Ô∏è Error recuperando datos del Clasificador de Tipos1");
}

// 3. Validaci√≥n y fallback SOLO si NO hay tipos
if (!Array.isArray(tipos)) tipos = [];

if (tipos.length === 0) {
    // Fallback: si el clasificador fall√≥, asignar Technical por defecto
    tipos = ["Technical Evaluation"];
    console.log("‚ö†Ô∏è Fallback: Clasificador fall√≥, usando Technical Evaluation por defecto");
}

// Forzar siempre 1 solo tipo (el primero detectado)
if (tipos.length > 1) {
    console.log(`‚ö†Ô∏è Clasificador devolvi√≥ ${tipos.length} tipos: ${tipos.join(', ')}. Usando solo el primero.`);
    tipos = [tipos[0]];
}

// Normalizar tipos: mapear tipos antiguos a los 3 v√°lidos
const VALID_TYPES = ['Technical Evaluation', 'Economical Evaluation', 'Others'];
tipos = tipos.map(t => {
    if (VALID_TYPES.includes(t)) return t;
    if (t.includes('Deliverables') || t.includes('FEED')) return 'Others';
    return 'Others';
});

// Eliminar duplicados
tipos = [...new Set(tipos)];

// 4. Nombre del Proyecto
let finalProjectName = nombreProyecto || metadataProjectName;

if (!finalProjectName || finalProjectName === "Sin Nombre" || finalProjectName.includes("La Zaida")) {
    finalProjectName = fileTitle.replace(/\.pdf$/i, '') + " (Project)";
}

console.log(`üìã Expandiendo ${tipos.length} tipos: ${tipos.join(', ')}`);

// 5. Generar Salida (Expandir items)
return tipos.map((tipo, idx) => ({
    json: {
        tipo_evaluacion: tipo,
        rfq_project_id: projectId,
        rfq_document_id: documentId,
        nombre_proyecto: finalProjectName,
        file_title: fileTitle,
        texto_rfq: fullText,
        indice: idx + 1,
        total_tipos: tipos.length
    }
}));

// =============================================
// NODO: Parsear Final (Con Nombre Proyecto)
// =============================================
const inputJson = $input.first().json;
// Asegurar que sea string
let llmOutput = "";
if (typeof inputJson === 'string') llmOutput = inputJson;
else if (inputJson.text && typeof inputJson.text === 'string') llmOutput = inputJson.text;
else if (inputJson.output && typeof inputJson.output === 'string') llmOutput = inputJson.output;
else if (inputJson.content && typeof inputJson.content === 'string') llmOutput = inputJson.content;
else llmOutput = JSON.stringify(inputJson.text || inputJson.output || inputJson.content || inputJson);

llmOutput = String(llmOutput || "[]");

// 1. Recuperar Contexto del Loop
let projectId = "unknown";
let documentId = "unknown";
let tipoEvaluacion = "Unknown";
let nombreProyecto = "Sin Nombre";

try {
    const loopData = $('Loop por Tipo1').first().json;
    projectId = loopData.rfq_project_id;
    documentId = loopData.rfq_document_id;
    tipoEvaluacion = loopData.tipo_evaluacion;
    nombreProyecto = loopData.nombre_proyecto || "Sin Nombre";
} catch(e) {}

// 2. L√≥gica de Parsing
let items = [];

try {
    let clean = llmOutput.replace(/```json/gi, '').replace(/```/g, '').trim();
    const start = clean.indexOf('[');
    const end = clean.lastIndexOf(']');
    if (start !== -1 && end !== -1) {
        items = JSON.parse(clean.substring(start, end + 1));
    }
} catch(e) {}

if (items.length === 0) {
    const lines = llmOutput.split('\n');
    let currentPhase = "General";
    for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;
        if (/^\d+\./.test(line) && !line.includes('Estimate')) {
            const text = line.replace(/^\d+\.\s*/, '').trim();
            if (i+1 < lines.length && (lines[i+1].trim().startsWith('-') || lines[i+1].trim().startsWith('‚Ä¢'))) {
                currentPhase = text;
            } else {
                 items.push({ fase: "General", requisito_rfq: text });
            }
        }
        else if (line.startsWith('-') || line.startsWith('‚Ä¢')) {
            items.push({ fase: currentPhase, requisito_rfq: line.replace(/^[-‚Ä¢]\s*/, '').trim() });
        }
    }
}

function generateId(str) {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
        hash = ((hash << 5) - hash) + str.charCodeAt(i);
        hash = hash & hash;
    }
    return 'item_' + Math.abs(hash).toString(16).padStart(12, '0');
}

return items.map(item => ({
    json: {
        rfq_project_id: projectId,
        project_name: nombreProyecto,
        rfq_document_id: documentId,
        evaluation: tipoEvaluacion,
        fase: item.fase || "General",
